$(function() {
    var a = $('#width'),
        b = $('#height'),
        c = $('#calculate'),
        d = $('#perimeter'),
        e = $('#area');
  
    c.click(function(){
      
      var w = Number(a.val()),
          h = Number(b.val());
      var f = 2 * (w + h),
          g = w * h;
      d.val(f);
      e.val(g);
      
    });
  });
  